// Helper function to get visible objects for a specific date
function getVisibleObjectsForDate(dateStr) {
    let formattedDate;
    if (dateStr) {
        const date = new Date(dateStr);
        formattedDate = date.toISOString().split('T')[0];
    } else {
        formattedDate = new Date().toISOString().split('T')[0];
    }

    const dayData = VISIBILITY_CALENDAR[formattedDate];

    if (!dayData) {
        return {
            date: formattedDate,
            north: null,
            south: null
        };
    }

    const northObject = dayData.north ? DSO_DETAILS[dayData.north] : null;
    const southObject = dayData.south ? DSO_DETAILS[dayData.south] : null;
    
    return {
        date: formattedDate,
        north: northObject,
        south: southObject
    };
}


